mapper.select:
select



             T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(field)}





                     T.id,


                     T.school_id,


                     T.active_name,


                     T.img_url,


                     T.content,


                     T.create_time,




         T.*


 from dm_active T


         T.school_id=#{schoolId}


        and T.active_name=#{activeName}


        and T.img_url=#{imgUrl}


        and T.content=#{content}


        and T.create_time=#{createTime}




    order by T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortField)} ${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortOrder)}


    limit #{pager.beginRow},#{pager.pageSize}

select count(*) from dm_active


         school_id=#{schoolId}


          and active_name=#{activeName}


        and img_url=#{imgUrl}


        and content=#{content}


        and create_time=#{createTime}

select



             T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(field)}





                     T.id,


                     T.school_id,


                     T.active_name,


                     T.img_url,


                     T.content,


                     T.create_time,




         T.*


 from dm_active T


         T.school_id=#{schoolId}


        and T.active_name=#{activeName}


        and T.img_url=#{imgUrl}


        and T.content=#{content}


        and T.create_time=#{createTime}

mapper.insert:
insert into dm_active
 (
    id,
    school_id,
    active_name,
    img_url,
    content,
    create_time
) values
 (
        #{id}
        ,
        #{schoolId}
        ,
        #{activeName}
        ,
        #{imgUrl}
        ,
        #{content}
        ,
        now()

 )

mapper.delete:
delete from dm_active


         id=#{id}


        and school_id=#{schoolId}


        and active_name=#{activeName}


        and img_url=#{imgUrl}


        and content=#{content}


        and create_time=#{createTime}

mapper.select:
select T.id,T.active_name,T.img_url,T.create_time from dm_active T


        T.school_id=#{schoolId}


        and T.active_name like concat('%${activeName}%')


        and T.img_url=#{imgUrl}


        and T.content=#{content}


        and T.create_time=#{createTime}



    order by T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortField)} ${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortOrder)}


    limit #{pager.beginRow},#{pager.pageSize}

select count(id) from dm_active


        school_id=#{schoolId}


        and active_name like concat('%${activeName}%')


        and img_url=#{imgUrl}


        and content=#{content}


        and create_time=#{createTime}

select T.id,T.active_name,T.img_url from dm_active T where T.school_id=#{schoolId}

    order by T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortField)} ${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortOrder)}


    limit #{pager.beginRow},#{pager.pageSize}
