mapper.select:
select



             T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(field)}





                     T.id,


                     T.school_id,


                     T.equipment_id,


                     T.class_id,


                     T.equipment_name,


                     T.new_version,


                     T.install_status,


                     T.down_status,


                     T.apk_url,


                     T.status,


                     T.version,


                     T.position,


                     T.remark,


                     T.create_time,


                     T.update_time,


                     T.teacher_id,


                     T.user_name,


                     T.password,


                     T.lock_status,




         T.*


 from dm_class_card T


         T.school_id=#{schoolId}


        and T.equipment_id=#{equipmentId}


        and T.class_id=#{classId}


        and T.equipment_name=#{equipmentName}


        and T.new_version=#{newVersion}


        and T.install_status=#{installStatus}


        and T.down_status=#{downStatus}


        and T.apk_url=#{apkUrl}


        and T.status=#{status}


        and T.version=#{version}


        and T.position=#{position}


        and T.remark=#{remark}


        and T.create_time=#{createTime}


        and T.update_time=#{updateTime}


        and T.teacher_id=#{teacherId}


        and T.user_name=#{userName}


        and T.password=#{password}


        and T.lock_status=#{lockStatus}




    order by T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortField)} ${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortOrder)}


    limit #{pager.beginRow},#{pager.pageSize}

select count(*) from dm_class_card T
LEFT JOIN jw_classes T3 ON T.class_id = T3.id


         T.school_id=#{schoolId}


        and T.equipment_id=#{equipmentId}


        and T3.grade_id=#{gradeId}


        and T.class_id=#{classId}


        and T.equipment_name=#{equipmentName}


        and T.new_version=#{newVersion}


        and T.install_status=#{installStatus}


        and T.down_status=#{downStatus}


        and T.apk_url=#{apkUrl}


        and T.status=#{status}


        and T.version=#{version}


        and T.position=#{position}


        and T.remark=#{remark}


        and T.create_time=#{createTime}


        and T.update_time=#{updateTime}


        and T.user_name=#{userName}


        and T.password=#{password}


        and T.lock_status=#{lockStatus}

select



             T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(field)}





                     T.id,


                     T.school_id,


                     T.equipment_id,


                     T.class_id,


                     T.equipment_name,


                     T.new_version,


                     T.install_status,


                     T.down_status,


                     T.apk_url,


                     T.status,


                     T.version,


                     T.position,


                     T.remark,


                     T.create_time,


                     T.update_time,


                     T.teacher_id,


                     T.user_name,


                     T.password,


                     T.lock_status,




         T.*,C.grade_id


 from dm_class_card T
 left join jw_classes C on T.class_id = C.id


         T.school_id=#{schoolId}


        T.id=#{id}


        and T.equipment_id=#{equipmentId}


        and T.class_id=#{classId}


        and T.equipment_name=#{equipmentName}


        and T.new_version=#{newVersion}


        and T.install_status=#{installStatus}


        and T.down_status=#{downStatus}


        and T.apk_url=#{apkUrl}


        and T.status=#{status}


        and T.version=#{version}


        and T.position=#{position}


        and T.remark=#{remark}


        and T.create_time=#{createTime}


        and T.update_time=#{updateTime}


        and T.teacher_id=#{teacherId}


        and T.user_name=#{userName}


        and T.password=#{password}


        and T.lock_status=#{lockStatus}

mapper.insert:
insert into dm_class_card
 (
    id,
    school_id,
    equipment_id,
    class_id,
    equipment_name,
    new_version,
    install_status,
    down_status,
    apk_url,
    status,
    version,
    position,
    remark,
    create_time,
    update_time,
    teacher_id,
    user_name,
    password,
    lock_status
) values
 (
        #{id}
        ,
        #{schoolId}
        ,
        #{equipmentId}
        ,
        #{classId}
        ,
        #{equipmentName}
        ,
        #{newVersion}
        ,
        #{installStatus}
        ,
        #{downStatus}
        ,
        #{apkUrl}
        ,
        #{status}
        ,
        #{version}
        ,
        #{position}
        ,
        #{remark}
        ,
        now()
        ,
        #{updateTime}
        ,
        #{teacherId}
        ,
        #{userName}
        ,
        #{password}
        ,
        #{lockStatus}

 )

mapper.delete:
delete from dm_class_card


         id=#{id}


        and school_id=#{schoolId}


        and equipment_id=#{equipmentId}


        and class_id=#{classId}


        and equipment_name=#{equipmentName}


        and new_version=#{newVersion}


        and install_status=#{installStatus}


        and down_status=#{downStatus}


        and apk_url=#{apkUrl}


        and status=#{status}


        and version=#{version}


        and position=#{position}


        and remark=#{remark}


        and create_time=#{createTime}


        and update_time=#{updateTime}


        and teacher_id=#{teacherId}


        and user_name=#{userName}


        and password=#{password}

mapper.select:
select
T.id,
T.equipment_name,
T.user_name,
T.password,
T.version,
T.new_version,
T.lock_status,
T3.grade_name AS gradeName,
T3.number AS className
from 	dm_class_card T
        left JOIN jw_classes T3 ON T.class_id = T3.id


        T.school_id=#{schoolId}


        and FIND_IN_SET( #{teacherId},T.teacher_id)


        and T.equipment_id=#{equipmentId}


        and T3.grade_id=#{gradeId}


        and T3.id=#{classId}


        and T.equipment_name=#{equipmentName}


        and T.install_status=#{installStatus}


        and T.down_status=#{downStatus}


        and T.version=#{version}


        and T.create_time=#{createTime}


        and T.user_name=#{userName}


        and T.password=#{password}


        and T.lock_status=#{lockStatus}


        AND concat(IFNULL(T.equipment_name, ''),
        IFNULL(T.user_name, ''),
        IFNULL(T.version, '')) LIKE concat(concat('%',#{keyWord}),'%')



    order by T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortField)} ${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortOrder)}


    limit #{pager.beginRow},#{pager.pageSize}

select * from dm_class_card where
     class_id=#{classId}

    and id  <>  #{id}

select T.id, T.school_id, T.equipment_id, T3.grade_id, T.class_id, T3.grade_name AS gradeName, T3.number AS classNumber, T.equipment_name, T.new_version, T.install_status, T.down_status, T.apk_url, T.status, T.version, T.position, T.remark, T.create_time, T.update_time, T.user_name, T.password, T.lock_status, T.teacher_id from dm_class_card T left JOIN jw_classes T3 ON T.class_id = T3.id where T.school_id=#{schoolId}
and T.install_status=#{installStatus}
and T3.grade_id=#{gradeId}
and T.class_id=#{classId}
AND concat(IFNULL(T.equipment_name, ''), IFNULL(T.equipment_id, ''), IFNULL(T.version, '')) LIKE concat(concat('%',#{keyWord}),'%')

select T.school_id,C.grade_id from dm_class_card T
left join jw_classes C on T.class_id = C.id
where T.user_name =#{userName} limit 1;

select T.id, T.school_id, T3.grade_id, T.user_name, T.teacher_id, T.apk_url, T.new_version, T.install_status, T3.grade_name AS gradeName, T3.number AS classNumber, T.class_id, T5.class_logan as motto, T5.class_alias as classAlias, T4.school_badge from dm_class_card T left JOIN jw_classes T3 ON T.class_id = T3.id LEFT JOIN school T4 ON T.school_id = T4.id LEFT JOIN dm_class_desc T5 ON T.class_id = T5.class_id where 1 = 1
and T.user_name=#{userName}
and T.password=#{password}
and T.equipment_name=#{equipmentName}
and T.id <> #{id}

mapper.update:
update dm_class_card
new_version=#{version}, apk_url=#{apkUrl}, status=#{status}, remark=#{remark}, install_status=#{installStatus}, down_status=#{downStatus}, position=#{position}
where user_name in
#{equipments}

update dm_class_card

    teacher_id= #{teacherId}

where class_id in

    #{rowData}
