mapper.insert:
insert into class_register
 (
    id,
    chapter_id,
    teacher_id,
    school_id,
    class_id,
    term,
    school_year,
    classroom_name,
    status,
    start_time,
    end_time,
    create_time
) values
 (
        #{id}
        ,
        #{chapterId}
        ,
        #{teacherId}
        ,
        #{schoolId}
        ,
        #{classId}
        ,
        #{term}
        ,
        #{schoolYear}
        ,
        #{classroomName}
        ,
        #{status}
        ,
         now()
        ,
        #{endTime}
        ,
        now()

 )

mapper.delete:
delete from class_register


         id=#{id}


        and chapter_id=#{chapterId}


        and teacher_id=#{teacherId}


        and school_id=#{schoolId}


        and grade_id=#{gradeId}


        and class_id=#{classId}


        and term=#{term}


        and school_year=#{schoolYear}


        and classroom_name=#{classroomName}


        and status=#{status}


        and start_time=#{startTime}


        and end_time=#{endTime}


        and create_time=#{createTime}

mapper.select:
SELECT DISTINCT t1.classroom_name, t1.school_year, t1.term, c.grade_name, c.`number` AS classes_name, t4.subject_name, t3.course_name, t5.name, t1.create_time, t1.start_time, t1.end_time FROM class_register t1 INNER JOIN jw_classes c ON c.`id` = t1.`class_id` LEFT JOIN jw_teacher_classes t2 ON t1.class_id = t2.classes_id LEFT JOIN jy_teaching_plan t3 ON t1.chapter_id = t3.chapter_id LEFT JOIN jy_textbook_setting t4 ON t3.textbook_id = t4.textbook_id LEFT JOIN jw_teacher t5 ON t5.id = t1.teacher_id
t1.school_id=#{schoolId} and t1.teacher_id=#{teacherId}
and c.grade_id=#{gradeId}
and t2.classes_id=#{classId}
and t4.subject_name=#{subjectName}
and t1.create_time >= #{createTime1}
and t1.create_time <= #{createTime2}
AND concat(IFNULL(t1.classroom_name, ''), IFNULL(t5.name, ''), IFNULL(t3.course_name, ''), IFNULL(t4.subject_name, ''), IFNULL(c.grade_name, '')) LIKE concat(concat('%',#{keyWord}),'%')
GROUP BY t1.id
order by t1.${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortField)} ${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortOrder)}
limit #{pager.beginRow},#{pager.pageSize}

SELECT DISTINCT t1.classroom_name, c.grade_name, t1.school_year, t1.term, c.`number` AS classes_name, t4.subject_name, t3.course_name, t5.name, t1.create_time, t1.start_time, t1.end_time FROM class_register t1 INNER JOIN jw_classes c ON c.`id` = t1.`class_id` LEFT JOIN jw_teacher_classes t2 ON t1.class_id = t2.classes_id LEFT JOIN jy_teaching_plan t3 ON t1.chapter_id = t3.chapter_id LEFT JOIN jy_textbook_setting t4 ON t3.textbook_id = t4.textbook_id LEFT JOIN jw_teacher t5 ON t5.id = t1.teacher_id
t1.school_id=#{schoolId} and t1.teacher_id=#{teacherId}
and c.grade_id=#{gradeId}
le and t2.classes_id=#{classId}
and t4.subject_name=#{subjectName}
and t1.create_time >= #{createTime1}
and t1.create_time <= #{createTime2}
AND concat(IFNULL(t1.classroom_name, ''), IFNULL(t5.name, ''), IFNULL(t3.course_name, ''), IFNULL(t4.subject_name, ''), IFNULL(c.grade_name, '')) LIKE concat(concat('%',#{keyWord}),'%')
GROUP BY t1.id

mapper.update:
update class_register


        status=#{status},


where
 status=1
 and create_time  >= #{createTime1}
 and create_time  <= #{createTime2}
