mapper.select:
select * from mes_inspect_record
where object_id = #{objectId}
and create_time between DATE_SUB(NOW(),INTERVAL 15 MINUTE) and now()
and institution_id in

    #{item}

SELECT mir.id AS id,mir.object_id AS objectId,mir.object_type AS objectType, mir.object_name AS objectName, mir.object_img_url AS objectImgUrl, mir.institution_parent_name AS institutionParentName, mir.institution_id AS institutionId, mir.happen_date AS happenDate, mir.create_time AS createTime, mir.record_user_id AS recordUserId, mir.record_user_name AS recordUserName, mir.record_user_type AS recordUserType, mir.institution_score AS institutionScore, mir.class_id AS classId, mir.school_id AS schoolId, mir.institution_name AS institutionName, mir.institution_desc AS institutionDesc, mia.`status` AS miaStatus FROM mes_inspect_record AS mir LEFT JOIN mes_institution_audit AS mia ON mir.id = mia.record_id where mir.class_id IN
#{teacherPosts.classId}
and mir.object_type = #{objectType}
and mia.`status` = #{miaStatus}
and mir.school_id = #{schoolId}
order by mir.create_time desc
limit #{pager.beginRow},#{pager.pageSize}

SELECT count(*) FROM
mes_inspect_record AS mir
LEFT JOIN mes_institution_audit AS mia ON mir.id = mia.record_id
where mir.class_id IN

    #{teacherPosts.classId}


    and mir.object_type = #{objectType}


    and mia.`status` = #{miaStatus}


    and mir.school_id = #{schoolId}

order by mir.create_time desc

select id, object_id, object_name, institution_parent_name, institution_name, institution_score, happen_date, create_time, institution_desc from mes_inspect_record where object_type=#{objectType} and record_user_id=#{recordUserId} and date_format(create_time,'%Y-%m')=#{createTime} and school_id=#{schoolId}
and class_id=#{classId}
order by create_time desc

select sum(institution_score) from mes_inspect_record T1
where T1.class_id=#{id} and T1.happen_date >= #{beginTime}  and T1.happen_date <= #{endTime} and institution_id IN (
SELECT I.id from mes_institution I LEFT JOIN
mes_time_status S1 ON S1.id=I.time_status_id where S1.`status`=1 AND S1.school_id=#{schoolId} and I.`level`=2
);

select * from school_year


        and to_year=#{toYear} and now() >= next_term_begin


        and school_id=#{schoolId}

SELECT T1.*,T3.id as timeStatusId from mes_inspect_record T1 LEFT JOIN mes_institution T2 ON T1.institution_id=T2.id LEFT JOIN mes_time_status T3 on T2.time_status_id=T3.id where
T1.institution_id=#{institutionId} and
T1.school_id = #{schoolId} and T1.class_id =#{classId} and T1.happen_date >= #{beginTime} and T1.happen_date < #{endTime}

        select sum(institution_score) from mes_inspect_record T1
        where T1.class_id=#{id} and T1.happen_date >= #{beginTime}  and T1.happen_date <= #{endTime} and institution_id IN (
	SELECT id from mes_institution where parent_id=#{institutionId}
	);

select *,1 scoreType from mes_inspect_record where class_id=#{classId}

    and create_time like concat('%',#{createTime},'%')

and institution_score > 0 order by happen_date DESC

select *,0 scoreType from mes_inspect_record where class_id=#{classId}

    and create_time like concat('%',#{createTime},'%')

and institution_score < 0 order by happen_date DESC
