mapper.select:
        SELECT R.id from jw_teacher T inner join jw_teacher_jw_role TR on T.id=TR.teacher_id
                            inner join jw_role R on TR.role_id=R.id
														where T.id=#{id}

select T.`name`,T.tel,T.sex,T.img_url imgUrl, GROUP_CONCAT(DISTINCT B.`name`) teacherjob,
GROUP_CONCAT(DISTINCT C.alias,'老师') teacherClassName
from jw_teacher T left join jw_teacher_classes A on T.id=A.teacher_id
left join jw_teacher_post B on  T.id = B.teacher_id
left join jw_course C on C.id = A.course_id and A.school_id=C.school_id


        T.school_id=#{schoolId}


        and T.id !=#{id}


        and T.name like concat('%',#{name},'%')


        and A.classes_id=#{classId}


        and T.status=#{status}

    and T.person_type = 1

GROUP BY T.id
order by convert(T.name using gbk) asc,T.tel asc

    limit #{pager.beginRow},#{pager.pageSize}

