mapper.select:
select T.id,T.`name`,T.tel,T.sex,T.img_url imgUrl,T.register_status registerStatus,T.weixin weixin,T.qq qq,T.email email, GROUP_CONCAT(DISTINCT B.`name`) teacherjob,
GROUP_CONCAT(DISTINCT C.alias,'老师') teacherClassName
from jw_teacher T left join jw_teacher_classes A on T.id=A.teacher_id
left join jw_teacher_post B on  T.id = B.teacher_id
left join jw_course C on C.id = A.course_id AND A.`school_id`=C.`school_id`


        T.school_id=#{schoolId}


        and T.name like "%"#{name}"%"


        and A.classes_id=#{classId}


        and T.status=#{status}


        and T.register_status=#{registerStatus}

    and T.person_type = 1

GROUP BY T.id
order by T.create_time desc

    limit #{pager.beginRow},#{pager.pageSize}

select

,GROUP_CONCAT(d.`name`) departmentName

,tii.`img`,tii.`status` AS checkStatus

from jw_teacher T
INNER JOIN jw_department_teacher  dt ON T.`id` = dt.`teacher_id`
INNER JOIN jw_department d ON d.`id` = dt.`department_id`

    LEFT JOIN (SELECT * FROM xw_teacher_image_input tii


            tii.`status`= #{checkStatus}


            AND tii.`school_id` = #{schoolId}


    ) tii ON tii.`teacher_id` = T.`id`





