SELECT T.id, T.`name`, ( SELECT group_concat( CONCAT( a.NAME, j.number, '班' ) ORDER BY a.id asc ,j.number asc) FROM dm_class_card d LEFT JOIN jw_classes j ON d.class_id = j.id LEFT JOIN ad_dd a ON j.grade_id = a.id WHERE d.school_id = #{schoolId} AND find_in_set( T.id, d.teacher_id ) ) AS assignedClass FROM jw_teacher T WHERE T.`status` = #{status} AND T.school_id = #{schoolId} AND T.person_type = #{personType}
and T.name like concat('%',#{name},'%')
GROUP BY T.id
limit #{pager.beginRow},#{pager.pageSize}

SELECT ,GROUP_CONCAT(CONCAT(tc.`grade_name`,' ',tc.`number`,'班',' ',tc.`alias`) ORDER BY tc.`grade_id`,tc.`number`) AS teaching FROM jw_teacher T LEFT JOIN (SELECT DISTINCT tc.`teacher_id`,c.`grade_name`,c.`grade_id`,c.`number`,jc.alias FROM jw_teacher_classes tc INNER JOIN jw_classes c ON c.`id` = tc.`classes_id` INNER JOIN jw_course jc ON jc.id = tc.`course_id` and jc.`school_id` = tc.`school_id`) tc ON tc.`teacher_id` = T.`id`
AND tc.grade_id = #{gradeId}
GROUP BY T.`id`
