mapper.select:
select



            T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(field)}





                T.id,


                T.school_year,


                T.plan_name,


                T.school_id,


                T.teacher_id,


                T.semester,


                T.grade_id,


                T.subject_id,


                T.teaching_plan_id,


                T.create_time,


                T.update_time,


                T.update_count,


                T.comment_count,


                T.studying_id,




        T.*


from jy_collective_plan T




                T.school_year like concat('%',#{schoolYear},'%')


                T.school_year=#{schoolYear}






                and T.plan_name like concat('%',#{planName},'%')


                and T.plan_name=#{planName}






                and T.school_id like concat('%',#{schoolId},'%')


                and T.school_id=#{schoolId}






                and T.teacher_id like concat('%',#{teacherId},'%')


                and T.teacher_id=#{teacherId}




        and T.semester=#{semester}




                and T.grade_id like concat('%',#{gradeId},'%')


                and T.grade_id=#{gradeId}






                and T.subject_id like concat('%',#{subjectId},'%')


                and T.subject_id=#{subjectId}






                and T.teaching_plan_id like concat('%',#{teachingPlanId},'%')


                and T.teaching_plan_id=#{teachingPlanId}






                and T.create_time like concat('%',#{createTime},'%')


                and T.create_time=#{createTime}






                and T.update_time like concat('%',#{updateTime},'%')


                and T.update_time=#{updateTime}




        and T.update_count=#{updateCount}


        and T.comment_count=#{commentCount}




                and T.studying_id like concat('%',#{studyingId},'%')


                and T.studying_id=#{studyingId}





    order by T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortField)} ${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortOrder)}


    limit #{pager.beginRow},#{pager.pageSize}

select count(*) from jy_collective_plan T




                T.school_year like concat('%',#{schoolYear},'%')


                T.school_year=#{schoolYear}






                and T.plan_name like concat('%',#{planName},'%')


                and T.plan_name=#{planName}






                and T.school_id like concat('%',#{schoolId},'%')


                and T.school_id=#{schoolId}






                and T.teacher_id like concat('%',#{teacherId},'%')


                and T.teacher_id=#{teacherId}




        and T.semester=#{semester}




                and T.grade_id like concat('%',#{gradeId},'%')


                and T.grade_id=#{gradeId}






                and T.subject_id like concat('%',#{subjectId},'%')


                and T.subject_id=#{subjectId}






                and T.teaching_plan_id like concat('%',#{teachingPlanId},'%')


                and T.teaching_plan_id=#{teachingPlanId}






                and T.create_time like concat('%',#{createTime},'%')


                and T.create_time=#{createTime}






                and T.update_time like concat('%',#{updateTime},'%')


                and T.update_time=#{updateTime}




        and T.update_count=#{updateCount}


        and T.comment_count=#{commentCount}




                and T.studying_id like concat('%',#{studyingId},'%')


                and T.studying_id=#{studyingId}

select



            T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(field)}





                T.id,


                T.school_year,


                T.plan_name,


                T.school_id,


                T.teacher_id,


                T.semester,


                T.grade_id,


                T.subject_id,


                T.teaching_plan_id,


                T.create_time,


                T.update_time,


                T.update_count,


                T.comment_count,


                T.studying_id,




        T.*


from jy_collective_plan T


        T.school_year=#{schoolYear}


        and T.plan_name=#{planName}


        and T.school_id=#{schoolId}


        and T.teacher_id=#{teacherId}


        and T.semester=#{semester}


        and T.grade_id=#{gradeId}


        and T.subject_id=#{subjectId}


        and T.teaching_plan_id=#{teachingPlanId}


        and T.create_time=#{createTime}


        and T.update_time=#{updateTime}


        and T.update_count=#{updateCount}


        and T.comment_count=#{commentCount}


        and T.studying_id=#{studyingId}

mapper.insert:
insert into jy_collective_plan
(
id,
school_year,
plan_name,
school_id,
teacher_id,
semester,
grade_id,
subject_id,
teaching_plan_id,
create_time,
update_time,
update_count,
comment_count,
studying_id
) values
(
#{id}
,
#{schoolYear}
,
#{planName}
,
#{schoolId}
,
#{teacherId}
,
#{semester}
,
#{gradeId}
,
#{subjectId}
,
#{teachingPlanId}
,
now()
,
#{updateTime}
,
#{updateCount}
,
#{commentCount}
,
#{studyingId}

)

mapper.delete:
delete from jy_collective_plan


        id=#{id}


        and school_year=#{schoolYear}


        and plan_name=#{planName}


        and school_id=#{schoolId}


        and teacher_id=#{teacherId}


        and semester=#{semester}


        and grade_id=#{gradeId}


        and subject_id=#{subjectId}


        and teaching_plan_id=#{teachingPlanId}


        and create_time=#{createTime}


        and update_time=#{updateTime}


        and update_count=#{updateCount}


        and comment_count=#{commentCount}


        and studying_id=#{studyingId}

mapper.select:
SELECT
*
FROM
jy_collective_plan
WHERE
plan_name = #{planName}
AND school_year= #{schoolYear}
AND school_id = #{schoolId}

    and id  <>  #{id}

SELECT t3.id, t3.plan_name, t3.create_time, t3.subjectName, t3.gradeName, t3.semester, t3.school_year, SUM(t3.sta = 0) AS planCount FROM ( SELECT T.id, T.plan_name, T.create_time, T.school_year, T.semester, T3.NAME AS subjectName, T4.NAME AS gradeName, IFNULL( T2.`status`, 1 ) AS sta FROM jy_collective_plan T LEFT JOIN jy_teacher_collection T2 on T.id = T2.collection_plan_id LEFT JOIN subject_material T3 on T3.id = T.subject_id LEFT JOIN subject_material T4 on T4.id = T.grade_id LEFT JOIN jy_team_teaching_plan T5 on T5.id = T2.teacher_plan_id WHERE T.teacher_id = #{teacherId}
and T.school_year = #{schoolYear}
) t3 GROUP BY t3.id ORDER BY t3.create_time desc
limit #{pager.beginRow},#{pager.pageSize}

SELECT T.id, T.plan_name, T.create_time FROM jy_collective_plan T left join jy_collective_plan_teacher T2 on T2.collective_plan_id = T.id
T2.teacher_id = #{teacherId}
and T.school_year = #{schoolYear}
GROUP BY T.id ORDER BY T.create_time DESC
