,
#{schoolId}
,
#{teacherId}
,
#{textbookId}
,
#{textbookName}
,
#{chapterId}
,
#{chapterName}
,
#{courseName}
,
#{teachTarget}
,
#{tearchPoint}
,
#{tearchMethod}
,
#{teachDifficulty}
,
#{tearchProcess}
,
#{itemPackageIds}
,
