)

mapper.select:
SELECT t1.id, t1.school_id, t1.teacher_id, t1.textbook_id, t1.textbook_name, t1.chapter_id, t1.chapter_name, t1.course_name, t2.NAME AS teacherName, IFNULL( t4.comment_count, 0 ) AS commentCount, IFNULL( t4.update_count, 0 ) AS updateCount, t4.create_time AS createTime, t1.update_time AS updateTime FROM jy_team_teaching_plan t1 LEFT JOIN jy_teacher_collection t4 ON t1.id = t4.teacher_plan_id LEFT JOIN jy_collective_plan_teacher t5 ON t1.teacher_id = t5.teacher_id LEFT JOIN jy_collective_plan t3 ON t3.id = t4.collection_plan_id LEFT JOIN jw_teacher t2 ON t2.id = t5.teacher_id
t4.status=#{status} AND t3.id = #{collectivePlanId}
GROUP BY t1.id ORDER BY t4.create_time DESC
limit #{pager.beginRow},#{pager.pageSize}

SELECT t1.id, t1.school_id, t1.teacher_id, t1.textbook_id, t1.textbook_name, t1.chapter_id, t1.chapter_name, t1.course_name, t1.create_time FROM jy_teaching_plan t1 WHERE t1.teacher_id =#{teacherId}
AND concat( IFNULL(t1.course_name, '')) LIKE concat(concat('%',#{courseName}),'%')
AND t1.course_name NOT IN ( SELECT t2.course_name FROM jy_teacher_collection t1, jy_team_teaching_plan t2 WHERE t1.collection_plan_id = #{collectivePlanId} AND t1.teacher_plan_id = t2.id ) GROUP BY t1.id ORDER BY t1.create_time DESC
limit #{pager.beginRow},#{pager.pageSize}

SELECT
id
FROM
jy_collective_plan

    teacher_id =  #{teacherId}
    AND id = #{collectivePlanId}
