mapper.insert:
insert into jy_prep_lesson_resource_file
 (
    id,
    filename,
    file_url,
    file_type,
    download_count,
    update_time,
    create_time,
    admin_id,
    admin_name,
    size,
    cover,
    num_look
) values
 (
        #{id}
        ,
        #{filename}
        ,
        #{fileUrl}
        ,
        #{fileType}
        ,
        #{downloadCount}
        ,
        now()
        ,
        now()
        ,
        #{adminId}
        ,
        #{adminName}
        ,
        #{size}
        ,
        #{cover}
         ,
        #{num_look}
 )

