mapper.select:
select



            T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(field)}





                T.id,


                T.teacher_id,


                T.school_id,


                T.file_id,


                T.url,


                T.name,


                T.type,


                T.browse_number,


                T.download_number,


                T.commend_number,


                T.create_time,


                T.file_size,


                T.update_time,


                T.share_status,




        T.*


from jy_resouces T




                T.teacher_id like concat('%',#{teacherId},'%')


                T.teacher_id=#{teacherId}






                and T.school_id like concat('%',#{schoolId},'%')


                and T.school_id=#{schoolId}






                and T.file_id like concat('%',#{fileId},'%')


                and T.file_id=#{fileId}






                and T.url like concat('%',#{url},'%')


                and T.url=#{url}






                and T.name like concat('%',#{name},'%')


                and T.name=#{name}




        and T.type=#{type}


        and T.browse_number=#{browseNumber}


        and T.download_number=#{downloadNumber}


        and T.commend_number=#{commendNumber}




                and T.create_time like concat('%',#{createTime},'%')


                and T.create_time=#{createTime}






                and T.file_size like concat('%',#{fileSize},'%')


                and T.file_size=#{fileSize}






                and T.update_time like concat('%',#{updateTime},'%')


                and T.update_time=#{updateTime}




        and T.share_status=#{shareStatus}



    order by T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortField)} ${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortOrder)}


    limit #{pager.beginRow},#{pager.pageSize}

select count(*) from jy_resouces T




                T.teacher_id like concat('%',#{teacherId},'%')


                T.teacher_id=#{teacherId}






                and T.school_id like concat('%',#{schoolId},'%')


                and T.school_id=#{schoolId}






                and T.file_id like concat('%',#{fileId},'%')


                and T.file_id=#{fileId}






                and T.url like concat('%',#{url},'%')


                and T.url=#{url}






                and T.name like concat('%',#{name},'%')


                and T.name=#{name}




        and T.type=#{type}


        and T.browse_number=#{browseNumber}


        and T.download_number=#{downloadNumber}


        and T.commend_number=#{commendNumber}




                and T.create_time like concat('%',#{createTime},'%')


                and T.create_time=#{createTime}






                and T.file_size like concat('%',#{fileSize},'%')


                and T.file_size=#{fileSize}






                and T.update_time like concat('%',#{updateTime},'%')


                and T.update_time=#{updateTime}




        and T.share_status=#{shareStatus}

select



            T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(field)}





                T.id,


                T.teacher_id,


                T.school_id,


                T.file_id,


                T.url,


                T.name,


                T.type,


                T.browse_number,


                T.download_number,


                T.commend_number,


                T.create_time,


                T.file_size,


                T.update_time,


                T.share_status,




        T.*


from jy_resouces T


        T.teacher_id=#{teacherId}


        and T.school_id=#{schoolId}


        and T.file_id=#{fileId}


        and T.url=#{url}


        and T.name=#{name}


        and T.type=#{type}


        and T.browse_number=#{browseNumber}


