mapper.select:
select



             T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(field)}





                     T.id,


                     T.school_id,


                     T.teacher_id,


                     T.textbook_id,


                     T.textbook_name,


                     T.chapter_id,


                     T.chapter_name,


                     T.course_name,


                     T.teach_target,


                     T.tearch_point,


                     T.tearch_method,


                     T.teach_difficulty,


                     T.tearch_process,


                     T.item_package_ids,


                     T.download_count,


                     T.view_count,


                     T.create_time,


                     T.update_time,




         T.*


 from jy_teaching_plan T


         T.school_id=#{schoolId}


        and T.share_status=#{shareStatus}


        and T.teacher_id=#{teacherId}


        and T.textbook_id=#{textbookId}


        and T.textbook_name=#{textbookName}


        and T.chapter_id=#{chapterId}


        and T.chapter_name=#{chapterName}


        and T.course_name=#{courseName}


        and T.teach_target=#{teachTarget}


        and T.tearch_point=#{tearchPoint}


        and T.tearch_method=#{tearchMethod}


        and T.tearch_process=#{tearchProcess}


        and T.item_package_ids=#{itemPackageIds}


        and T.download_count=#{downloadCount}


        and T.view_count=#{viewCount}


        and T.create_time=#{createTime}


        and T.update_time=#{updateTime}




    order by T.${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortField)} ${@cn.hutool.core.util.StrUtil@toUnderlineCase(pager.sortOrder)}


    limit #{pager.beginRow},#{pager.pageSize}

select count(*) from jy_teaching_plan


         school_id=#{schoolId}


        and share_status=#{shareStatus}


        and teacher_id=#{teacherId}


        and textbook_id=#{textbookId}


        and textbook_name=#{textbookName}


        and chapter_id=#{chapterId}


        and chapter_name=#{chapterName}


        and course_name=#{courseName}


        and teach_target=#{teachTarget}


        and tearch_point=#{tearchPoint}


        and tearch_method=#{tearchMethod}


        and tearch_process=#{tearchProcess}


        and item_package_ids=#{itemPackageIds}


        and download_count=#{downloadCount}


        and view_count=#{viewCount}


        and create_time=#{createTime}


        and update_time=#{updateTime}

mapper.insert:
insert into jy_teaching_plan
 (
    id,
    school_id,
    teacher_id,
    textbook_id,
    textbook_name,
    chapter_id,
    chapter_name,
    course_name,
    teach_target,
    tearch_point,
    tearch_method,
    teach_difficulty,
    tearch_process,
    item_package_ids,
    download_count,
    view_count,
    create_time,
    update_time
) values
 (
        #{id}
        ,
        #{schoolId}
        ,
        #{teacherId}
        ,
        #{textbookId}
        ,
        #{textbookName}
        ,
        #{chapterId}
        ,
        #{chapterName}
        ,
        #{courseName}
        ,
        #{teachTarget}
        ,
        #{tearchPoint}
        ,
        #{tearchMethod}
        ,
        #{teachDifficulty}
        ,
        #{tearchProcess}
        ,
        #{itemPackageIds}
        ,
        #{downloadCount}
        ,
        #{viewCount}
        ,
        now()
        ,
        #{updateTime}

 )
