mapper.select:
SELECT ast.`status` as status, aou.warehouse_name as usePlace, aou.apply_user_name as dutyPersonName, aou.apply_user_department_name as dutyPersonDept, aou.get_out_time as borrowTime FROM xw_assets_out_new aou LEFT JOIN xw_assets_stock_detail ast ON aou.assets_id = ast.id
and ast.id = #{id}
and aou.school_id=#{schoolId}
order by aou.id desc

SELECT count(*) FROM xw_assets_out_new aou LEFT JOIN xw_assets_stock_detail ast ON aou.assets_id = ast.id
and ast.id = #{id}
and aou.school_id=#{schoolId}

SELECT T.* from xw_assets_stock_detail T
T.id in
#{assetsResId}

SELECT
    concat(ROUND(

    ( SELECT count( 1 ) AS useCount FROM xw_assets_stock_detail t1  WHERE t1.del = 1 AND school_id=#{schoolId} and t1.status = 2) /
    ( SELECT count( 1 ) AS totalCount FROM xw_assets_stock_detail t1 WHERE t1.del = 1 AND school_id=#{schoolId}

    )*100,2)

    ,'','%')

select t.assets_name,sum(t.assets_price) totalPrice from xw_assets_stock_detail t where t.del = 1 and t.school_id = #{schoolId}
and assets_property=#{assetsProperty}
and DATE_FORMAT(t.put_in_time,'%Y-%m-%d') >= DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 30 DAY),'%Y-%m-%d') and DATE_FORMAT(t.put_in_time,'%Y-%m-%d') <= DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 0 DAY),'%Y-%m-%d')
and DATE_FORMAT(t.put_in_time,'%Y-%m') >= DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 12 MONTH),'%Y-%m') and DATE_FORMAT(t.put_in_time,'%Y-%m') <= DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 0 MONTH),'%Y-%m')
group by t.assets_name
